
folder = fileparts(mfilename('fullpath'));

% this build works with Matlab 2014a 64-bit using modified MATLAB headers
[notfound,warnings] = loadlibrary(fullfile(libPath,'eyelink_core64.dll'),...
    fullfile(folder, 'core_expt.h'), ...
    'mfilename', fullfile(folder, 'eyelink.m'), ...
    'addheader','eye_data', ...
    'addheader','eyetypes', ...
    'addheader','eyelink', ...    
    'includepath',folder...              
    );
