
libPath = 'C:\Windows\System32\';
folder = fileparts(mfilename('fullpath'));
hdrPath = fullfile(folder, 'include64');

% this build works with Matlab 2014a 64-bit using modified MATLAB headers
[notfound,warnings] = loadlibrary(fullfile(libPath,'kernel32.dll'),...
    fullfile(hdrPath,'\windows'),...              
    'h_kernel.h',...              
    'mfilename','win_kernel32', ...
    'addheader','winbase', ...
    'addheader','winuser', ...
    'includepath',hdrPath...              
    );
