/* fcntl.h - file i/o control constants                                      */
/*---------------------------------------------------------------------------*/
#define _O_FAIL          0x0000
#define _O_OPEN          0x0001
#define _O_REPL          0x0002
#define C_CREAT         0x0010

#define _O_RDONLY        0x0000
#define _O_WRONLY        0x0001
#define _O_RDWR          0x0002
#define _O_APPEND        0x0004
#define _O_CREAT         0x0008
#define _O_TRUNC         0x0010
#define _O_EXCL          0x0020
#define _O_TEXT          0x0040
#define _O_BINARY        0x0800
#define O_BINARY         0x0800
